body_template:
{
    "caller_id": "AuthMind Service",
    "short_description": "AuthMind [{{.IssueType | jsonescape}}]: {{.Title | default "Security Issue Detected" | jsonescape}}",
    "description": "AuthMind Summary: {{.Details.Summary | jsonescape}}

Issue Details:
{{range .Details.Results}}--- 
Message: {{.Message | jsonescape}}
Risk: {{.Risk | jsonescape}}
Incidents: {{.Incidents | marshal | jsonescape}}
{{end}}

Footer: {{.Footer | jsonescape}}",
    "impact": "{{if eq .Severity 4}}1{{else if eq .Severity 3}}1{{else if eq .Severity 2}}2{{else}}3{{end}}",
    "urgency": "{{if eq .Severity 4}}1{{else if eq .Severity 3}}2{{else if eq .Severity 2}}2{{else}}3{{end}}",
    "work_notes": "AuthMind Issue ID: {{.IssueID | jsonescape}}
Initial Risk Score: {{.Risk | jsonescape}}
First Seen: {{.FirstSeen | jsonescape}}
Event Timestamp: {{.Timestamp | jsonescape}}
Issue Keys: {{.IssueKeys | marshal | jsonescape}}",
    "contact_type": "automated",
    "category": "Security"
}

{
    "blocks": [
        {
            "type": "header",
            "text": {
                "type": "plain_text",
                "text": "🛡️ Security Issue: {{.IssueType | jsonescape}}",
                "emoji": true
            }
        },
        {
            "type": "section",
            "text": {
                "type": "mrkdwn",
                "text": "*Description:*
{{.IssueMessage | jsonescape}}"
            }
        },
        {
            "type": "section",
            "fields": [
                {
                    "type": "mrkdwn",
                    "text": "*Risk Level:*
{{.Risk | jsonescape}}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Domain:*
{{index .IssueKeys "domain" | default "N/A" | jsonescape}}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Total Flows:*
{{.FlowCount}}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Incidents:*
{{.IncidentCount}}"
                }
            ]
        },
        {
            "type": "divider"
        },
        {
            "type": "section",
            "text": {
                "type": "mrkdwn",
                "text": "*Primary Incident Details*"
            }
        },
        {
            "type": "section",
            "fields": [
                {
                    "type": "mrkdwn",
                    "text": "*Identity:*
👤 {{.UserEmail | jsonescape}}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Asset:*
🖥️ {{index .IssueKeys "asset_name" | default "N/A" | jsonescape}}"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Status:*
🟢 Open"
                },
                {
                    "type": "mrkdwn",
                    "text": "*Site Code:*
{{index .IssueKeys "site_code" | default "N/A" | jsonescape}}"
                }
            ]
        },
        {
            "type": "context",
            "elements": [
                {
                    "type": "mrkdwn",
                    "text": "📅 *First seen:* {{.FirstSeen | jsonescape}}  |  *Last seen:* {{.Timestamp | jsonescape}}"
                }
            ]
        },
        {
            "type": "actions",
            "elements": [
                {
                    "type": "button",
                    "text": {
                        "type": "plain_text",
                        "text": "View Incident in AuthMind",
                        "emoji": true
                    },
                    "style": "primary",
                    "url": "{{.IncidentsURL}}"
                }
            ]
        }
    ]
}

{
  "requestedFor": ["{{.UserEmail}}"],
  "requestType": "REVOKE_ACCESS",
  "requestedItems": [
    {
      "id": "{{index .IssueKeys "entitlement_id" | default "REPLACE_ME"}}",
      "type": "ENTITLEMENT"
    }
  ],
  "comment": "Automatic revocation triggered by AuthMind remediation engine."
}
